$(function () {

    var $variantsEdit = $('.variantsEdit');
    var $tableProperties = $('.tableProperties tbody');
    var $productVariantsForm = $('.productVariantsForm');
    var $prodPropList = $('.prodPropList');
    var $btnPropertiesUpdate = $('.btnPropertiesUpdate');

    if ($variantsEdit.length) {
        var variantsUpdateUrl = $('.btnVariantsUpdate').data('url-update');

        // обновить блок с вариантами
        $variantsEdit.on('click', '.btnVariantsUpdate', function () {
            $('.productVariantsForm').load(variantsUpdateUrl);
        });

        // обновление с добавлением варианта
        $(document).on('click', '.btnAddVariant', function () {
            var hash = $(this).data('variant-hash');
            $('.productVariantsForm').load(variantsUpdateUrl + '?add=' + hash);
        });

        $(document).on('click', '.btnRemoveVariant', function () {
            var id = $(this).parents('.variantItem').data('variant-id');
            $('.productVariantsForm').load(variantsUpdateUrl + '?remove=' + id);
        });

        $productVariantsForm.on('change', '.variantItem .VarInput', function () {
            $(this).closest('tr').find('.btnApplyVarChanges').css({'visibility': 'visible'});
        });

        $productVariantsForm.on('click', '.btnApplyVarChanges', function () {
            var $button = $(this),
                $btntr = $button.closest('tr'),
                url = $button.data('url-save'),
                var_id = $btntr.data('variant-id'),
                var_Price = $btntr.find('.varPrice').val(),
                var_NetCommission = $btntr.find('.varNetCommission').val(),
                var_Article = $btntr.find('.varArticle').val(),
                var_Quantity = $btntr.find('.varQuantity').val();
            $button.css({'visibility': 'hidden'});
            $.ajax({
                url: url,
                data: {
                    id: var_id,
                    price: var_Price,
                    netCommission: var_NetCommission,
                    sku: var_Article,
                    quantity: var_Quantity
                },
                success: function (response) {
                    if (response.status)$productVariantsForm.load(variantsUpdateUrl);
                }
            });
        });
    }

    if ($prodPropList.length) {

        var PropertiesUpdateUrl = $btnPropertiesUpdate.data('url-update');

        $btnPropertiesUpdate.on('click', function () {
            $tableProperties.load(PropertiesUpdateUrl);
        });
        $tableProperties.on('change', '.propertyItem .optionInput', function () {
            $(this).closest('tr').find('.btnApplyPropChanges').css({'visibility': 'visible'});
        });
        $tableProperties.on('click', '.btnApplyPropChanges', function () {
            var $button = $(this),
                $btntr = $button.closest('tr'),
                url = $button.data('url-save'),
                prop_id = $btntr.data('property-id'),
                prop_name = $btntr.find('.propName').val(),
                prop_val = $btntr.find('.propVal').val();
            $button.css({'visibility': 'hidden'});
            $.ajax({
                url: url,
                data: {
                    id: prop_id,
                    name: prop_name,
                    value: prop_val
                },
                success: function (response) {
                    if (response.status)$tableProperties.load(PropertiesUpdateUrl);
                }
            });
        });

        $tableProperties.on('click', '.btnRemoveProperty', function () {
            var $button = $(this);
            var url = $button.data('url-property-remove'),
                prop_id = $button.closest('tr').data('property-id');
            $.ajax({
                url: url,
                data: {
                    id: prop_id
                },
                success: function (response) {
                    if (response.status)$button.closest('tr').remove();
                }
            });
        });
        $prodPropList.on('click', '.btnAddOption', function () {
            $('#PropTbl').css({'height': '51px'});
        });
        $prodPropList.on('click', '.btnAddNewProperty', function () {
            $('#PropTbl').css({'height': '0px'});
            var url = $(this).data('url-property-add'),
                prop_name = $('#prop-name').val(),
                prop_val = $('#prop-val').val();
            $.ajax({
                url: url,
                data: {
                    name: prop_name,
                    value: prop_val
                },
                success: function (response) {
                    if (response.status)$tableProperties.load(PropertiesUpdateUrl);
                }
            });
        });
    }

});
