function subscribeUpdateRegionsList($selectCountry, $selectRegion) {

    if (!$selectCountry.length) return;

    // функция обновления списка регионов
    var updateRegionsList = function () {
        var idCounty = $selectCountry.val();
        $.ajax({
            url: $('.updateRegionsList').data('url'),
            method: 'get',
            data: {
                idCountry: idCounty
            },
            success: function (response) {
                $selectRegion.empty();
                $selectRegion.append($('<option></option>'));
                $.each(response.regions, function (index, value) {
                    $selectRegion.append($('<option value="' + value.id + '">' + value.name + '</option>'));
                });
            }
        });
    };

    // обновить сразу
    updateRegionsList();

    // обновлять при смене страны
    $selectCountry.change(function () {
        updateRegionsList();
    });
}

